        }
        y1 = (this.func(x) - this.func(x - (1 * h))) / h
        y1 = y1.toFixed(6);


        y2 = (this.func(x) - 2 * this.func(x - (1 * h)) + this.func(x - (2 * h))) / Math.pow(h, 2)
        y2 = y2.toFixed(6);


        y3 = (this.func(x) - 3 * this.func(x - (1 * h)) + 3 * this.func(x - (2 * h)) - this.func(x - (3 * h))) / Math.pow(h, 3)
        y3 = y3.toFixed(6);

